package com.practice;

public class EncapsulationEg {
	
	public static int add() {
		
		int result;
		
		int a,b;
		
		a = 5;
		b = 6;
		
		result = a + b;
		
		System.out.println(result);
		return result;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub		
		EncapsulationEg.add();
	}
	
}
